--SELECT STATEMENT ON BUYERS TABLE
SELECT * FROM [ESTATE_SALES_SCHEMA].[BUYERS];

--SELECT STATEMENT ON REAL ESTATE PROPERTY SALES TABLE
SELECT * FROM [ESTATE_SALES_SCHEMA].[REAL ESTATE PROPERTY SALES];

-- SELECT STATEMENT WITH FULL OUTER JOIN TO SHOW ALL ROWS
SELECT *
FROM [ESTATE_SALES_SCHEMA].[REAL ESTATE PROPERTY SALES] AS SALES 
FULL OUTER JOIN [ESTATE_SALES_SCHEMA].[BUYERS] AS BUYERS
ON SALES.[BUYER ID] = BUYERS.[BUYER ID];